import { Component, OnInit } from '@angular/core';
import { articulos } from 'src/app/models/articulos.model';
import { ArticulosService } from 'src/app/services/articulos.service';

@Component({
  selector: 'app-articulos-list',
  templateUrl: './articulos-list.component.html',
  styleUrls: ['./articulos-list.component.css']
})
export class ArticulosListComponent implements OnInit {
  articulos?: articulos[];
  currentArticulos?: articulos;
  currentIndex = -1;
  nombre = '';
  currentImage = "";
  currentImagenes : String[] = [];

  constructor(private articulosService: ArticulosService) { }

  ngOnInit(): void {
    this.retrieveArticulos();
  }

  retrieveArticulos(): void {
    this.articulosService.getAll()
      .subscribe(
        data => {
          this.articulos = data;
          console.log(data);
        },
        error => {
          console.log(error);
        });
  }

  refreshList(): void {
    this.retrieveArticulos();
    this.currentArticulos = undefined;
    this.currentIndex = -1;
  }

  setActiveArticulos(articulos: articulos, index: number): void {
    this.currentArticulos = articulos;
    this.currentIndex = index;
    this.currentImage = "0";
    this.currentImagenes = [];
    if (articulos.imagenes) {
      try {
        articulos.imagenes.forEach(imagen => {
          this.currentImagenes.push("http://localhost:8080/../../imagenes/" + imagen);
        });
      } catch(e) {}
    }

  }

  removeAllArticulos(): void {
    this.articulosService.deleteAll()
      .subscribe(
        response => {
          console.log(response);
          this.refreshList();
        },
        error => {
          console.log(error);
        });
  }

  searchNombre(): void {
    var objeto = Object.create({});
    objeto.nombre = this.nombre;
    this.articulosService.findByFiltro(objeto)
      .subscribe(
        data => {
          this.articulos = data;
          console.log(data);
        },
        error => {
          console.log(error);
        });
  }
  getImage(articulo:articulos): void {
    const data = {
      id_imagen:  articulo.id_imagen
    };
    this.articulosService.findImage(data)
      .subscribe(
        data => {
          this.articulos = data;
          console.log(data);
        },
        error => {
          console.log(error);
        });
  }

  scroll(id: any) {
    var el = document.getElementById("imagen" + String(id));
    this.currentImage = String(id);
    if (el)
      el.scrollIntoView(false);
  }
  scrollImage() {
    var siguiente = imagenes.length > parseInt(this.currentImage) ? 0 : parseInt(this.currentImage);
    this.currentImage = siguiente;
    var el = document.getElementById("imagen" + String(id));
    this.currentImage = String(id);
    if (el)
      el.scrollIntoView(false);
  }
}
