import { Component, OnInit } from '@angular/core';
import { articulos } from 'src/app/models/articulos.model';
import { ArticulosService } from 'src/app/services/articulos.service';
import { ViewEncapsulation } from '@angular/core';
import { ElementRef, HostListener, ViewChild } from '@angular/core';
import AOS from "aos";
@Component({
  selector: 'app-pagina-inicial',
  templateUrl: './pagina-inicial.component.html',
  styleUrls: ['./pagina-inicial.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class PaginaInicialComponent implements OnInit {
  articulos?: articulos[];
  currentArticulos?: articulos;
  currentIndex = -1;
  nombre = '';
  currentImage = "";
  currentImagenes : String[] = [];
  scrollAnterior = false;
  lastScrollTop = 0;
  animacion = false;

  constructor(private articulosService: ArticulosService) { }

  ngOnInit(): void {
  }
  @HostListener('document:scroll', ['$event'])
  animacionImagenes() {
    if (!this.animacion) {
      var st = window.pageYOffset || document.documentElement.scrollTop; 
    if (st > this.lastScrollTop) {
      document.getElementById("img3")?.scrollIntoView({ behavior: 'smooth', block: 'center' });
    } else if (st < this.lastScrollTop) {
      $('html, body').animate({
        scrollTop: $("#elementId").offset().top
    }, 1000);
      document.onanimationend()
      document.getElementById("img2")?.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
    this.lastScrollTop = st <= 0 ? 0 : st; // For Mobile or negative scrolling
    }
    }
    
}
